function searchSubbrach() {
    var data = $(this).serializeObject();
    $('#subbranch-list').pagination({
        url: $$.wrapUrl('/action/ms/bank-subbranch/list'),
        pageNumber: 1,
        queryParams: data,
        onLoadSuccess: function(data) {
            var html = template('subbranch-templ', data);
            $('#subbranch-list').html(html);                
        }
    }).infinite().on('infinite', function () {
        $('#subbranch-list').pagination('next');
    });;
}


$(function() {
    //支行查询
    $('#subbranch-sb').searchbar({
        onSearch: searchSubbrach
    });

    $('#subbranch-list').on('click', 'a.weui-cell', function() {
        //$('#subbranch_name').val($(this).attr('data-text'));
        $('#subbranch-popup').popup('close');
    });

    $$.request('/action/ms/parameter/list', {
        debit_fee_algo: '1',
        credit_fee_algo: '1',
        equi_type: '1',
        account_bank: '1'
    }, function(data) {
        if (data.errcode == 0) {
            $("#equi_type").select2({
                title: '请选择机具类型',
                items: data.equi_type
            });

            $("#district").select({
                title: '请选择装机地区',
                items: district
            });

            $("#fee_algo").select({
                title: '请选择借记卡手续费',
                items: data.debit_fee_algo
            });

            $("#credit_fee_algo").select({
                title: '请选择贷记卡手续费',
                items: data.credit_fee_algo
            });



            // $("#bank_code").select({
            //     title: "选择开户银行",
            //     items: getItems(data.account_bank),
            //     onChange: function() {
            //         $('#search-fm').trigger('submit');
            //     }
            // });
        }
    });

});
